import UIKit
let DidChangeModuleDHeightNotification: Notification.Name =
    Notification.Name("DidChangeModuleDHeightNotification")
let userInfoModuleDHeight:String = "ModuleDHeight"

class ModuleDViewController: UIViewController {
    @IBOutlet weak var label: UILabel!


    
    override func viewDidAppear(_ animated: Bool) {
        super.viewDidAppear(animated)
        NotificationCenter.default.post(name: DidChangeModuleDHeightNotification, object: nil, userInfo: [userInfoModuleDHeight:self.label.frame.height])
        
    }
    
    override func viewDidLoad() {
        
        
        super.viewDidLoad()

        label.numberOfLines = 0
        label.text = ""        // Do any additional setup after loading the view.
    }
    

